function selectPay(e) {
    let options = document.getElementsByClassName('option')
    for (i = 0; i < options.length; i++) {
        options[i].style.display = 'none'
        options[i].previousElementSibling.style.backgroundColor = '#fff'
        options[i].previousElementSibling.children[0].style.fill = 'none'
    }

    e.nextElementSibling.style.display = 'flex'
    e.style.backgroundColor = '#e1e1e5'
    e.children[0].style.fill = '#333'
}

function insertHolder(e, prefix) {
    e.value = e.value.toUpperCase()
    if (prefix != undefined) {
        document.getElementById(prefix + '-holder').textContent = e.value
    }
}

function insertCardNumber(e, prefix) {
    if (e.value.length > 16) {
        e.value = e.value.substr(0, 16)
        if (prefix != undefined) {
            document.getElementById(prefix + '-number').textContent = e.value.substr(0, 4) + ' ' + e.value.substr(4, 4) +
                ' ' + e.value.substr(8, 4) + ' ' + e.value.substr(12, 4)
            return
        }
    }
    e.value = e.value.replace(/\D+/g, '')
    if (prefix != undefined) {
        document.getElementById(prefix + '-number').textContent = e.value.substr(0, 4) + ' ' + e.value.substr(4, 4) + ' ' +
            e.value.substr(8, 4) + ' ' + e.value.substr(12, 4)
    }
}

function insertExpMonth(e, prefix) {
    if (e.value.length > 2) {
        e.value = e.value.substr(0, 2)
    }
    e.value = e.value.replace(/\D+/g, '')
    if (prefix != undefined) {
        document.getElementById(prefix + '-card-exp-month').textContent = e.value
    }
}

function insertExpYear(e, prefix) {
    if (e.value.length > 4) {
        e.value = e.value.substr(0, 4)
    }
    e.value = e.value.replace(/\D+/g, '')
    if (prefix != undefined) {
        document.getElementById(prefix + '-card-exp-year').textContent = e.value
    }
}

function insertSecurity(e, prefix) {
    if (e.value.length < 1) {
        return
    }
    if (e.value.length > 4) {
        e.value = e.value.substr(0, 4)
    }
    e.value = e.value.replace(/\D+/g, '')
    if (prefix != undefined) {
        document.getElementById(prefix + '-card-security').textContent = e.value
    }
}

function frontCard(e, prefix) {
    document.getElementsByClassName(prefix + '-frente')[0].children[0].style.transform = 'rotateY(0)'
}

function backCard(e, prefix) {
    document.getElementsByClassName(prefix + '-verso')[0].children[0].style.transform = 'rotateY(0)'
}